
public class PracticeStrings {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String name = "Srishti";
		System.out.println(name);
		System.out.println("Hello," + " How are you doing!!");
		char a = name.charAt(4);
		System.out.println(a);
	    System.out.println(name.charAt(2));
	    System.out.println(name.compareToIgnoreCase("srishti"));
	    System.out.println(name.concat(" Sachdeva"));
	    System.out.println(name.endsWith("ti"));
	    System.out.println(name.equals("Srishtihg"));
	    //System.out.println(name.format()));
	    //System.out.println(name.getChars(4, 6, 'd', 3));
	    System.out.println(name.length());
	    System.out.println(name.hashCode());
	    System.out.println(name.indexOf("h"));
	    System.out.println(name.toLowerCase());
	    System.out.println(name.toUpperCase());
	    
	    
	}

}
